package com.optima.opticarrier.printer;

import java.util.Locale;

import javax.inject.Named;
import javax.inject.Singleton;

import com.optima.opticarrier.domain.BtRequest;
import com.optima.opticarrier.domain.BtRequest_;
import com.optima.opticarrier.printer.support.GenericPrinter;

/**
 * {@link GenericPrinter} for {@link BtRequest} 
 *
 * @see GenericPrinter
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class BtRequestPrinter extends GenericPrinter<BtRequest> {
    public BtRequestPrinter() {
        super(BtRequest.class, BtRequest_.xml, BtRequest_.status);
    }

    @Override
    public String print(BtRequest btRequest, Locale locale) {
        if (btRequest == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, btRequest.getXml());
        appendIfNotEmpty(ret, btRequest.getStatus());
        return ret.toString();
    }
}