package com.optima.opticarrier.printer;

import java.util.Locale;

import javax.inject.Named;
import javax.inject.Singleton;

import com.optima.opticarrier.domain.Prefissi;
import com.optima.opticarrier.domain.Prefissi_;
import com.optima.opticarrier.printer.support.GenericPrinter;

/**
 * {@link GenericPrinter} for {@link Prefissi} 
 *
 * @see GenericPrinter
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class PrefissiPrinter extends GenericPrinter<Prefissi> {
    public PrefissiPrinter() {
        super(Prefissi.class, Prefissi_.comune, Prefissi_.provincia, Prefissi_.regione);
    }

    @Override
    public String print(Prefissi prefissi, Locale locale) {
        if (prefissi == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, prefissi.getComune());
        appendIfNotEmpty(ret, prefissi.getProvincia());
        appendIfNotEmpty(ret, prefissi.getRegione());
        return ret.toString();
    }
}