package com.optima.opticarrier.web.domain;

import javax.inject.Inject;
import javax.inject.Named;

import com.optima.opticarrier.domain.CpsQuoteResponse;
import com.optima.opticarrier.web.domain.support.GenericExcelExporter;
import com.optima.opticarrier.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link CpsQuoteResponse} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class CpsQuoteResponseExcelExporter extends GenericExcelExporter<CpsQuoteResponse> {
    @Inject
    protected CpsQuoteResponseSearchForm sf;

    public CpsQuoteResponseExcelExporter() {
        super("cpsQuoteResponse_codiceArea", "cpsQuoteResponse_descrizioneArea", "cpsQuoteResponse_quantitaRichesta",
                "cpsQuoteResponse_volumiMensiliAssegnati", "cpsQuoteResponse_tolleranzaMensile", "cpsQuoteResponse_volumiGiornalieriAssegnati");
    }

    @Override
    protected void fillResultItem(int row, CpsQuoteResponse item) {
        int col = 0;
        setValue(row, col++, item.getCodiceArea());
        setValue(row, col++, item.getDescrizioneArea());
        setValue(row, col++, item.getQuantitaRichesta());
        setValue(row, col++, item.getVolumiMensiliAssegnati());
        setValue(row, col++, item.getTolleranzaMensile());
        setValue(row, col++, item.getVolumiGiornalieriAssegnati());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();

        setSelector(row++, 0, "cpsQuoteResponse_codiceArea", sf.getCodiceAreaSelector());
        setSelector(row++, 0, "cpsQuoteResponse_descrizioneArea", sf.getDescrizioneAreaSelector());
        setRangeNumber(row++, 0, "cpsQuoteResponse_quantitaRichesta", sf.getQuantitaRichestaRange());
        setRangeNumber(row++, 0, "cpsQuoteResponse_volumiMensiliAssegnati", sf.getVolumiMensiliAssegnatiRange());
        setRangeNumber(row++, 0, "cpsQuoteResponse_tolleranzaMensile", sf.getTolleranzaMensileRange());
        setRangeNumber(row++, 0, "cpsQuoteResponse_volumiGiornalieriAssegnati", sf.getVolumiGiornalieriAssegnatiRange());
        setRangeNumber(row++, 0, "cpsQuoteResponse_tolleranzaGiornaliera", sf.getTolleranzaGiornalieraRange());
        setRangeNumber(row++, 0, "cpsQuoteResponse_giorniLavorativi", sf.getGiorniLavorativiRange());
        setSelectedEntities(row++, 0, "cpsQuoteResponse_file", sf.getFileSelector().getSelected());
        setSelectedEntities(row++, 0, "cpsQuoteResponse_olo", sf.getOloSelector().getSelected());
    }
}