package com.optima.opticarrier.web.domain;

import javax.inject.Inject;
import javax.inject.Named;

import com.optima.opticarrier.domain.OloCps;
import com.optima.opticarrier.web.domain.support.GenericExcelExporter;
import com.optima.opticarrier.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link OloCps} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class OloCpsExcelExporter extends GenericExcelExporter<OloCps> {
    @Inject
    protected OloCpsSearchForm sf;

    public OloCpsExcelExporter() {
        super("oloCps_codiceOlo", "oloCps_oloDescrizione", "oloCps_oloDtinival", "oloCps_oloDtfinval", "oloCps_codiceTelecom");
    }

    @Override
    protected void fillResultItem(int row, OloCps item) {
        int col = 0;
        setValue(row, col++, item.getCodiceOlo());
        setValue(row, col++, item.getOloDescrizione());
        setDateTime(row, col++, item.getOloDtinival());
        setDateTime(row, col++, item.getOloDtfinval());
        setValue(row, col++, item.getCodiceTelecom());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();

        setSelector(row++, 0, "oloCps_codiceOlo", sf.getCodiceOloSelector());
        setSelector(row++, 0, "oloCps_oloDescrizione", sf.getOloDescrizioneSelector());
        setRangeDateTime(row++, 0, "oloCps_oloDtinival", sf.getOloDtinivalRange());
        setRangeDateTime(row++, 0, "oloCps_oloDtfinval", sf.getOloDtfinvalRange());
        setSelector(row++, 0, "oloCps_codiceTelecom", sf.getCodiceTelecomSelector());
    }
}