    @Test
    public void manyToOne_setRequest() {
        WlrRequestAggiuntivis many = new WlrRequestAggiuntivis();

        // init
        SbcWlrRequest one = new SbcWlrRequest();
        one.setId(ValueGenerator.getUniqueInteger());
        many.setRequest(one);

        // make sure it is propagated properly
        assertThat(many.getRequest()).isEqualTo(one);

        // now set it to back to null
        many.setRequest(null);

        // make sure null is propagated properly
        assertThat(many.getRequest()).isNull();
    }

